mapper.insert:
insert into user (userId, name, sex, 
  birthday, call, jiluzt
  )
values (#{userid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{sex,jdbcType=INTEGER}, 
  #{birthday,jdbcType=DATE}, #{call,jdbcType=INTEGER}, #{jiluzt,jdbcType=VARCHAR}
  )

mapper.select:
select userId, name, sex, birthday, call, jiluzt
from user
where userId = #{userid,jdbcType=INTEGER}
